import React, { Component } from 'react';
import './Button.css';

class Button extends Component {
  isOperator = val => {
    return !isNaN(val) || val === "." || val === "=";
  };

  render(){
    return(
      <div 
        className={`button ${this.isOperator(this.props.children) ? "" : "operator"}`}
      >
        {this.props.children}
      </div>
    );
  }
}

export default Button;
